import numpy as np

class Solution(object):
    def reconstructQueue(self, people):
        """
        :type people: List[List[int]]
        :rtype: List[List[int]]
        """
        if people == []:
            return []
        heights = [i[0] for i in people]
        compare = [i[1] for i in people]
        new_heights = [None] * len(heights)
        new_compare = [None] * len(heights)
        #print heights, compare, new_compare, new_heights
        while len(heights) != 0:
            current = np.argmin(heights)
            destination = compare[current]
            i = 0
            while i < destination:
                if new_heights[i] != None and new_heights[i] != heights[current]:
                    destination += 1
                i += 1
            while new_heights[destination] != None:
                destination += 1
            new_compare[destination] = compare[current]
            new_heights[destination] = heights[current]
            del heights[current]
            del compare[current]
        new_people = np.stack((new_heights, new_compare), axis = 1)
        return new_people.tolist()

